import React from 'react';
import NumericInput from 'react-numeric-input';
import VoiceIcon from '../icons/utx_ico_obtain_00.png';
import DanceIcon from '../icons/utx_ico_obtain_01.png';
import VisualIcon from '../icons/utx_ico_obtain_02.png';
import { lsTest } from '../utils';

function defaultProState() {
    return {
        version: 26,
        currentState: "Voice",
        show: false,
        general: {
            idolStats: [60, 60, 60],
            idolMult: [1.15, 1.15, 1.15],
            memStats: [10, 10, 10],
            memMult: [1.05, 1.05, 1.05],
            vocalLessons: [635, 3, 2, 0],
            danceLessons: [605, 2, 2, 0], 
            visualLessons: [460, 2, 0, 1],
            spRate: 0.05,
            rest: 3,
            gift: 2,
            date: 2,
            shop: 2,
            classroom: 3,
            classroomStats: [110, 100, 80],
            drink: 3,
            upgrade: [3,4],
            cardAcq: [6, 9, 4],
            removal: 2,
        },
        voice: {
            type: 0,
        },
        dance: {
            type: 1,
        },
        visual: {
            type: 2,
        },
    }
}

function defaultMasterState() {
    return {
        version: 18,
        currentState: "speed",
        show: false,
        general: {
            idolStats: [60, 60, 60],
            idolMult: [1.15, 1.15, 1.15],
            memStats: [10, 10, 10],
            memMult: [1.05, 1.05, 1.05],
            vocalLessons: [635, 3, 2, 0],
            danceLessons: [605, 2, 2, 0],
            visualLessons: [460, 2, 0, 1],
            spRate: 0.05,
            rest: 3,
            gift: 2,
            date: 2,
            shop: 2,
            classroom: 3,
            classroomStats: [110, 100, 80],
            drink: 3,
            upgrade: [3, 4],
            cardAcq: [6, 9, 4],
            removal: 2,
        },
        voice: {
            type: 0,
        },
        dance: {
            type: 1,
        },
        visual: {
            type: 2,
        },
    }
}

class Weights extends React.Component {
    constructor(props) {
        super(props);

        this.onSettingChanged = this.onSettingChanged.bind(this);
        this.onGeneralSettingChanged = this.onGeneralSettingChanged.bind(this);
        this.onTypeChanged = this.onTypeChanged.bind(this);
        this.onCapChanged = this.onCapChanged.bind(this);
        this.onMinimumChanged = this.onMinimumChanged.bind(this);
        this.onToggleWeights = this.onToggleWeights.bind(this);
        this.onMotivationChanged = this.onMotivationChanged.bind(this);
        this.onProReset = this.onProReset.bind(this);
        this.onMasterReset = this.onMasterReset.bind(this);

        this.state = defaultProState();
        this.props.onChange(this.state[this.state.currentState], this.state.general);
    }

    componentDidUpdate(prevProps, prevState) {
        if(prevState && prevState !== this.state && lsTest()) {
            window.localStorage.setItem("weights", JSON.stringify(this.state));
        }
    }

    onProReset() {
        let newState = defaultProState();
        this.setState(newState);
        this.props.onChange(newState[newState.currentState], newState.general);
    }

    onMasterReset() {
        let newState = defaultMasterState();
        this.setState(newState);
        this.props.onChange(newState[newState.currentState], newState.general);
    }

    onSettingChanged(event, numberString, numberInput) {
        if (!event) return;

        let settings = this.state[this.state.currentState];

        if (typeof event === "number") {
            if (numberInput.id.indexOf('.') > 0) {
                let split = numberInput.id.split('.');
                settings[split[0]][split[1]] = event;
            } else {
                settings[numberInput.id] = event;
            }
        }
        else {
            settings[event.target.id] = !settings[event.target.id];
        }

        let newSettings = {};
        newSettings[this.state.currentState] = settings;
        this.setState(newSettings);

        this.props.onChange(settings, this.state.general);
    }

    onGeneralSettingChanged(event, numberString, numberInput) {
        if (!event) return;

        let settings = this.state.general;

        if (typeof event === "number") {
            if (numberInput.id.indexOf('.') > 0) {
                let split = numberInput.id.split('.');
                settings[split[0]][split[1]] = event;
            } else {
                settings[numberInput.id] = event;
            }
        }
        else {
            settings[event.target.id] = !settings[event.target.id];
        }

        let newSettings = {};
        newSettings.general = settings;
        this.setState(newSettings);

        this.props.onChange(this.state[this.state.currentState], settings);
    }

    onMotivationChanged(event) {
        let settings = this.state.general;
        settings.motivation = event.target.value;
        let newSettings = {};
        newSettings.general = settings;
        this.setState(newSettings);
        this.props.onChange(this.state[this.state.currentState], settings);
    }

    onTypeChanged(event) {
        this.setState({
            currentState: event.target.id
        });

        this.props.onChange(this.state[event.target.id], this.state.general);
    }

    onCapChanged(event) {
        let settings = this.state[this.state.currentState];
        settings.cap = event.target.value;
        let newSettings = {};
        newSettings[this.state.currentState] = settings;
        this.setState(newSettings);
        this.props.onChange(settings, this.state.general);
    }

    onMinimumChanged(event) {
        let settings = this.state[this.state.currentState];
        settings.minimum = event.target.value;
        let newSettings = {};
        newSettings[this.state.currentState] = settings;
        this.setState(newSettings);
        this.props.onChange(settings, this.state.general);
    }

    onToggleWeights(event) {
        this.setState({show: !this.state.show});
    }

    render() {
        return (
            <div className="weights">
                <div className="weight-row">
                    <input id="voice" type="image" className={this.state.currentState == "speed" ? "image-btn selected" : "image-btn"} src={VoiceIcon} onClick={this.onTypeChanged} alt="Voice"/>
                    <input id="dance" type="image" className={this.state.currentState == "stamina" ? "image-btn selected" : "image-btn"} src={DanceIcon} onClick={this.onTypeChanged} alt="Dance"/>
                    <input id="visual" type="image" className={this.state.currentState == "power" ? "image-btn selected" : "image-btn"} src={VisualIcon} onClick={this.onTypeChanged} alt="Visual"/>
                </div>
                <div className="weight-row">
                    <button id="weights-toggle" type="button" onClick={this.onToggleWeights}>{this.state.show ? "Hide Settings" : "Customize Settings"}</button>
                </div>
                {
                    this.state.show &&
                    <>
                    <div className="weight-row">
                        <div class="section-header">Scenario</div>
                        <div class="section-explanation">
                            Which scenario you're playing in.<br/>
                            Changes the stat gains from trainings and some default values.
                        </div>
                        <button id="reset-weights-GL" type="button" onClick={this.onProReset}>GM</button>
                        <button id="reset-weights-GL" type="button" onClick={this.onMasterReset}>GL</button>
                    </div>
                    <div className="weight-row">
                            <div className="section-header">Starting Stats</div>
                            <div className="section-explanation">
                            Starting stats.
                        </div>
                        <label for="races.0">Voice</label>
                        <NumericInput onChange={this.onGeneralSettingChanged} type="number" id="idolStats.0" value={this.state.general.idolStats[0]} min={0} max={200} step={5}/>
                        <label for="races.1">Dance</label>
                        <NumericInput onChange={this.onGeneralSettingChanged} type="number" id="idolStats.1" value={this.state.general.idolStats[1]} min={0} max={200} step={5}/>
                        <label for="races.2">Visual</label>
                        <NumericInput onChange={this.onGeneralSettingChanged} type="number" id="idolStats.2" value={this.state.general.idolStats[2]} min={0} max={200} step={5}/>
                    </div>
                    </>
                }
                <div className="weight-row">
                    <div className="section-header">Uma's Bonuses</div>
                    <div className="section-explanation">
                        The percentages on the uma's stat screen, converted to decimal. <br/>
                        For example, 10% is 1.1, and 15% is 1.15.
                    </div>

                </div>
            </div>
        );
    }
}

export default Weights;
