import React from 'react';
import NumericInput from 'react-numeric-input';
import SpeedIcon from '../icons/utx_ico_obtain_00.png';
import StaminaIcon from '../icons/utx_ico_obtain_01.png';
import PowerIcon from '../icons/utx_ico_obtain_02.png';
import GutsIcon from '../icons/utx_ico_obtain_03.png';
import WisdomIcon from '../icons/utx_ico_obtain_04.png';
import FriendIcon from '../icons/utx_ico_obtain_05.png';
import { lsTest } from '../utils';

function defaultProState() {
    return {
        version: 26,
        currentState: "speed",
        show: false,
        general: {
            idolStats: [60, 60, 60],
            idolMult: [1.15, 1.15, 1.15],
            memStats: [10, 10, 10],
            memMult: [1.05, 1.05, 1.05],
            vocalLessons: [635, 3, 2, 0],
            danceLessons: [605, 2, 2, 0], 
            visualLessons: [460, 2, 0, 1],
            spRate: 0.05,
            rest: 3,
            gift: 2,
            date: 2,
            shop: 2,
            classroom: 3,
            classroomStats: [110, 100, 80],
            drink: 3,
            upgrade: [3,4],
            cardAcq: [6, 9, 4],
            removal: 2,
        },
        speed: {
            type: 0,
        },
        stamina: {
            type: 1,
        },
        power: {
            type: 2,
        },
        guts: {
            type: 3,
        },
        wisdom: {
            type: 4,
        },
        friend: {
            type: 6,
        }
    }
}

function defaultMasterState() {
    return {
        version: 18,
        currentState: "speed",
        show: false,
        general: {
            bondPerDay: 20,
            races: [7,2,0,3],
            unbondedTrainingGain: [
                [8,0,4,0,0,2,19],
                [0,8,0,6,0,2,20],
                [0,4,9,0,0,2,20],
                [2,0,2,7,0,2,20],
                [2,0,0,0,6,3,0]
            ],
            bondedTrainingGain: [
                [11,0,5,0,0,2,23],
                [0,9,0,6,0,2,21],
                [0,4,10,0,0,2,21],
                [3,0,2,10,0,2,24],
                [3,0,0,0,9,3,0]
            ],
            summerTrainingGain: [
                [12,0,6,0,0,2,24],
                [0,12,0,8,0,2,25],
                [0,6,13,0,0,2,25],
                [3,0,3,11,0,2,25],
                [4,0,0,0,10,3,0]
            ],
            umaBonus: [1.06,1.06,1.06,1.06,1.06,1],
            multi: 1.4,
            bonusSpec: 20,
            motivation: 0.2,
            scenarioLink: [
                "ミホノブルボン",
                "ライトハロー",
                "スマートファルコン",
                "アグネスタキオン",
                "サイレンススズカ",
            ],
            scenarioBonus: 75,
            fanBonus: 0.05,
        },
        speed: {
            type: 0,
            stats: [1.1,1,1.2,1,1,0.5,1.5],
            cap:600,
            minimum: 35,
            prioritize: true,
            onlySummer: false,
        },
        stamina: {
            type: 1,
            stats: [1,1,1,1.1,1,0.5,1.5],
            cap:550,
            minimum: 35,
            prioritize: false,
            onlySummer: false,
        },
        power: {
            type: 2,
            stats: [1,1.1,1,1,1,0.5,1.5],
            cap:550,
            minimum: 35,
            prioritize: false,
            onlySummer: false,
        },
        guts: {
            type: 3,
            stats: [2,1,2,1,1,0.5,1.5],
            cap:550,
            minimum: 30,
            prioritize: true,
            onlySummer: false,
        },
        wisdom: {
            type: 4,
            stats: [1.1,1,1,1,1.1,0.5,1],
            cap:600,
            minimum: 30,
            prioritize: true,
            onlySummer: false,
        },
        friend: {
            type: 6,
            stats: [1,1,1,1,1,0.5,0.75],
            cap:500,
            minimum: 30,
        }
